DROP DATABASE IF EXISTS dz;
CREATE DATABASE dz;
USE dz;

-- tables
DROP TABLE IF EXISTS users;

CREATE TABLE users (
    id BIGINT UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY, 
    name VARCHAR(50),
    created_at DATETIME,
    updated_at DATETIME
) COMMENT 'пользователи';




-- tables
DROP TABLE IF EXISTS orders;

create table orders (
    id BIGINT UNSIGNED NOT NULL AUTO_INCREMENT PRIMARY KEY,
    product VARCHAR(50),
    buyer_id BIGINT UNSIGNED NOT NULL,
    created_at DATETIME,
    FOREIGN KEY (buyer_id) REFERENCES users(id)
);






SELECT DISTINCT  
(SELECT name FROM users WHERE id=ORDERS.BUYER_ID)  AS buyer
FROM orders ORDER BY buyer;