use dz;
truncate users;
create table users (
    id INT,
    name VARCHAR(50),
    birthday_at  DATE,
    created_at DATE,
    updated_at DATE
);



create or replace view users_up_to_date AS SELECT created_at FROM users ORDER BY created_at DESC LIMIT 5;
DELETE FROM users WHERE created_at not in (SELECT * FROM users_up_to_date);
SELECT * FROM users ORDER BY created_at DESC;